import React, { useState } from "react";
import "./App.css";

function App() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const handleSearch = async () => {
    if (!query.trim()) return;
    setLoading(true);
    setError("");
    setResults([]);

    try {
      const response = await fetch(
        `http://127.0.0.1:8000/scrape?search=${encodeURIComponent(query)}`
      );
      if (!response.ok) {
        throw new Error("Failed to fetch data");
      }
      const data = await response.json();
      setResults(data);
    } catch (err) {
      setError("Error fetching results.");
      console.error(err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1>üì¶ Metal Price Scraper</h1>
        <p>Search for stainless steel products on Made-in-China</p>
      </header>

      <div className="search-container">
        <input
          type="text"
          placeholder="e.g. stainless steel TP316L seamless pipe"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {loading && <p className="status">üîÑ Searching...</p>}
      {error && <p className="error">‚ùå {error}</p>}

      {results.length > 0 && (
        <div className="results-table">
          <table>
            <thead>
              <tr>
                <th>Product Title</th>
                <th>Supplier</th>
                <th>Price</th>
                <th>MOQ</th>
                <th>Link</th>
              </tr>
            </thead>
            <tbody>
              {results.map((item, index) => (
                <tr key={index}>
                  <td>{item.title}</td>
                  <td>{item.supplier}</td>
                  <td>{item.raw_price}</td>
                  <td>{item.moq}</td>
                  <td>
                    <a href={item.link} target="_blank" rel="noopener noreferrer">
                      üîó View
                    </a>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
