import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [progress, setProgress] = useState(0); // ✅ Added progress state

  const handleSearch = () => {
    if (!query.trim()) return;
    setLoading(true);
    setError("");
    setResults([]);
    setProgress(0); // ✅ Reset progress

    const source = new EventSource(
      `http://127.0.0.1:8000/scrape-stream?search=${encodeURIComponent(query)}`
    );

    source.onmessage = (event) => {
      const data = JSON.parse(event.data);

      if (data.progress !== undefined) {
        setProgress(data.progress); // ✅ Update progress
      }

      if (data.results) {
        setResults(data.results);
        setLoading(false);
        source.close();
      }

      if (data.error) {
        setError(data.error);
        setLoading(false);
        source.close();
      }
    };

    source.onerror = (err) => {
      setError("Error fetching results.");
      setLoading(false);
      console.error(err);
      source.close();
    };
  };

  return (
    <div className="app-container">
      <header className="header">
        <h1>📦 Metal Price Scraper</h1>
        <p>Search for stainless steel products on Made-in-China</p>
      </header>

      <div className="search-container">
        <input
          type="text"
          placeholder="e.g. stainless steel TP316L seamless pipe"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      {loading && (
        <div className="status">
          <p>🔄 Scraping in progress...</p>
          <progress max="100" value={progress}></progress> {/* ✅ Progress bar */}
        </div>
      )}

      {error && <p className="error">❌ {error}</p>}

      {results.length > 0 && (
        <div className="results-table">
          <table>
            <thead>
              <tr>
                <th>Product Title</th>
                <th>Supplier</th>
                <th>Price</th>
                <th>MOQ</th>
                <th>Link</th>
              </tr>
            </thead>
            <tbody>
              {results.map((item, index) => (
                <tr key={index}>
                  <td>{item.title}</td>
                  <td>{item.supplier}</td>
                  <td>{item.raw_price}</td>
                  <td>{item.moq}</td>
                  <td>
                    <a
                      href={item.link}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      🔗 View
                    </a>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default App;
